import React, {useEffect} from "react";
import "./content.scss";
import usePath from "../Hooks/usePath";

const getMarkup = () => require.context('./../markups', true, /\.*md/).keys()

const Content = ({markdown}) => {
    const [currentPath] = usePath();

    useEffect(()=>{
        //let pattern = /\.*.md$/;
        const pattern = /\d?\.playlist\/\d?\.edit\.md/
        let file = getMarkup().filter( path => path.test(pattern))
        console.log(file)
        console.log(currentPath);
    },[currentPath])

    return (
        <main className="introduction__content">
            I will be render from md;
        </main>
    )
}

export default Content;
