import React from "react";
import { connect } from "react-redux";
import { setPitch } from "../../../actions/actions";
import Slider from "./Slider/Slider";
import "./pitch-control.scss";

const PitchControl = props => {
    return (
        <div className={"pitch pitch-" + props.name} >
            <Slider from={-8} to={8} initValue={0} onChange={props.setPitch}/>
        </div>
    )
}

const mapDispachToProps = dispach => ({
    setPitch : (value) => dispach(setPitch(value)), 
})

export default connect(null, mapDispachToProps)(PitchControl);
