import { ACTIONS } from "../actions/actions";

const initState = {
    searchString: "",
    searchResult: {}
}

function searchReducer(state = initState, action){
    console.log(action)
    switch(action.type){
        case ACTIONS.SEARCH_INPUT : 
        return {
            ...state, 
            searchString : action.text
        }

        default : return state;
    }
}

export default searchReducer;
