import React, {useEffect, useState} from "react";
import ReactMarkdown from "react-markdown";
import "./content.scss";
import usePath from "../Hooks/usePath";
import parseHtmlClass from "../MarkDonwExtesnion/htmlClass";
import htmlClass from "./../MarkDonwExtesnion/reamark-hclass";

const getMarkups = () => require.context('./../markups', true, /\.*md/).keys()

const load = (module) => import(`./../markups/${module}`).then( module => module.default);

const Content = () => {
    const [currentPath] = usePath();
    const [md, setMd] = useState(null);

    useEffect(()=>{
        if(!currentPath.length) return;
        let pattern = "";
        currentPath.forEach( slug => pattern += "\\/?\\d?\\." + slug)
        pattern += "\\.md";
        let regex = new RegExp(pattern);
        let file = getMarkups().filter( path => regex.test(path))[0]?.replace("./", "");
        if(!file) return;
        load(file)
            .then(md => fetch(md))
            .then(response => response.text())
            .then(md => setMd(md))
    },[currentPath, setMd])

    if(!md) return null;

    return (
        <main className="introduction__content">
            <ReactMarkdown source={md} plugins={[parseHtmlClass, htmlClass]}/>{md}
        </main>
    )
}

export default Content;
