import React, { useCallback } from "react";
import {Link} from "react-router-dom"
import {Container, Row, Col, Button} from "react-bootstrap";
import classNames from "classnames";

const Advantage = ({header, text , link, image, side = "left"}) => {
    
    const columnSetup = useCallback((col)=> ({
            span : 6,
            order : (side === "left" || col === 2) ? 1 : 2
    }), [side])

    const animationSetup = useCallback( col => ({
        "data-aos": (side === "left" && col === 2) ? "fade-right" : "fade-left" 
    }), [side])


    const sectionClass = classNames("advantage", {
        "section-left": (side === "left"),
        "section-right": (side === "right")
    })

    if(!header || !text || !image ) return null;
    return (
        <section className={sectionClass}>
            <Container>
                <Row>
                    <Col sm={12} lg={columnSetup(1)}>
                        <div className="adv-section-image" {...animationSetup(1)}>
                            <img src={image} alt="advantages section"/>
                        </div>
                    </Col>
                    <Col sm={12} lg={columnSetup(2)}>
                        <div className="adv-section-content" {...animationSetup(2)}>
                            <div className="line"/>
                            <h4 className="adv-section-header"><b>{header.bold}</b>{header.rest}</h4>
                            <p className="adv-section-content text">{text}</p>
                                <Link to={link.to}><Button>{link.text}</Button></Link>
                        </div>
                    </Col>
                </Row>
            </Container>
        </section>
    )
}

export default Advantage;
