import { useMemo, useState, useEffect, useCallback } from "react"
import { getApi } from "./../../../apis/apiProvider";
import { PLAYBACK_STATE } from "./usePlabackState";

//getRecordUrl
class PlayerControler {
    progressUpdaterHandler = null;

    current = {
        id: null,
        start: 0,
        duration: 0,
        progress: 0,
        state: null,
        buffered: 0,
    };

    btnStateHandlers = {}
    progressWatchers = {}
    bufferedWatchers = []
    progressProviders = {};
    currentWatchers = [];

    setCurrent(nextCurrent){
        this.current = nextCurrent;
        this.spreadCurrentChange();
    }

    addCurrentWatcher(handler){
        this.currentWatchers.push(handler)
    }


    spreadCurrentChange(){
        for(let handler of this.currentWatchers){
            handler(this.current);
        }
    }
    //--------------

    addBtnCtrlHandler(id, handler) {
        this.btnStateHandlers[id] = handler;
    }

    setBtnState(id, state) {
        this.btnStateHandlers[id](state)
    }

    //----------------------

    addProgressWatcher(id, handler) {
        if (this.progressWatchers[id]) {
            this.progressWatchers[id].push(handler);
            
        } else{
            this.progressWatchers[id] = [handler];
        }
    }

    setProgress(id, progress) {
        for (let handler of 
            [...this.progressWatchers[id], 
             ...this.progressWatchers['#']]) {
            handler(progress)
        }
        this.current.progress = progress;
    }

    //-------------------

    addProgressProvider(id, handler) {
        this.progressProviders[id] = handler;
    }

    getProgress(id) {
        if(!id){
            return this.getProgress(this.current.id);
        }
        return this.progressProviders[id]();
    }

    // ---------------
    setBuffered(buffered){
        if(this.current.id){
            this.current.buffered = buffered;
        }
        this.spreadBufferChanges();
    }

    addBuffredWatcher(handel){
        this.bufferedWatchers.push(handel)
    }

    spreadBufferChanges(){
        for(let handler of this.bufferedWatchers){
            handler(this.current.buffered);
        }
    }
    
}


export function useRecordPlayer() {
    const [media, setMediaElement] = useState(null);
    // const ctr = useContext(PlayerContext);
    const [ctr, _] = useState(new PlayerControler())

    const api = useMemo(() => {
        return getApi('RecordsStore');
    }, [getApi])

    useEffect(() => {
        const medEl = document.createElement('audio');
        setMediaElement(medEl);
        ctr.mediaElement = medEl;
    }, [setMediaElement, ctr])

    const startUpdateProg = useCallback(() => {
        const handler = setInterval(() => {
            const mediaEl = media || ctr.mediaElement;
            const progress = mediaEl.currentTime / (ctr.current.duration / 1000);
            ctr.setProgress(ctr.current.id, progress);
            //--
            const bufferdTimeRanges = mediaEl.buffered;
            if(bufferdTimeRanges.length){
                const end = bufferdTimeRanges.end(bufferdTimeRanges.length-1);
                ctr.setBuffered(end / (ctr.current.duration / 1000))
            } 
        }, 200)
        ctr.progressUpdaterHandler = handler;
    }, [media, ctr])

    const stopUpdateProg = useCallback(() => {
        clearInterval(ctr.progressUpdaterHandler);
    }, [ctr])

    useEffect(()=>{
        if(media){
            media.addEventListener("ended", ()=>{
                if(ctr.current.state === PLAYBACK_STATE.PLAY){
                    ctr.setCurrent({
                        ...ctr.current,
                        state : PLAYBACK_STATE.PAUSE,
                    })
                    ctr.setBtnState(ctr.current.id, PLAYBACK_STATE.PAUSE)
                }
                stopUpdateProg();
            })
            media.addEventListener('progress', ()=>{
                const bufferdTimeRanges = media.buffered;
                if(bufferdTimeRanges.length){
                    const end = bufferdTimeRanges.end(bufferdTimeRanges.length-1);
                    ctr.setBuffered(end / (ctr.current.duration / 1000))
                } 
            })
        }
    }, [media, ctr])

    const playback = useCallback((id, pbState) => {
        if (pbState === PLAYBACK_STATE.PLAY) {
            const { progress, duration } = ctr.getProgress(id);
            const position = (duration / 1000) * progress;
            media.src = api.getRecordUrl(id);
            media.currentTime = position;
            media.play();
            if (ctr.current.id) {
                ctr.setBtnState(ctr.current.id, PLAYBACK_STATE.PAUSE)
            }
            ctr.setCurrent({
                id,
                duration,
                progress: progress,
                buffered: 0,
                state: PLAYBACK_STATE.PLAY,
            })
            startUpdateProg()
        } else if (pbState === PLAYBACK_STATE.PAUSE) {
            media.pause();
            ctr.setCurrent({
                ...ctr.current,
                state: PLAYBACK_STATE.PAUSE
            })
            stopUpdateProg();
        }
    }, [
        media,
        api,
        ctr,
        stopUpdateProg,
        startUpdateProg,
    ])

    const seek = useCallback(({ 
            id = ctr.current.id, 
            progress, 
            duration = ctr.current.duration 
        }) => {
        const mediaEl = media || ctr.mediaElement
        console.log("get seek order" , progress)

        if(!id || !duration){
            return;
        }

        if (ctr.current.id && ctr.current.id !== id) {
            ctr.setBtnState(ctr.current.id, PLAYBACK_STATE.PAUSE);
        }
        const position = (duration / 1000) * progress;

        if(isNaN(position)){
            return;
        }

        stopUpdateProg();
        if (ctr.current.id !== id) {
            mediaEl.src = api.getRecordUrl(id);
            mediaEl.currentTime = position;
            ctr.setBtnState(id, PLAYBACK_STATE.PLAY);
            ctr.setProgress(id, progress);
            ctr.setCurrent({
                ...ctr.current,
                id,
                duration,
                state: PLAYBACK_STATE.PLAY
            })
        }

        mediaEl.currentTime = position;
        if (mediaEl.pause) {
            mediaEl.play();
            ctr.setBtnState(ctr.current.id, PLAYBACK_STATE.PLAY);
            ctr.setCurrent({
                ...ctr.current,
                state: PLAYBACK_STATE.PLAY
            })
        }

        startUpdateProg();

    }, [ctr, api, media, stopUpdateProg, startUpdateProg])

    return [playback, seek, ctr]
}
