import React , { useCallback, useRef, useEffect } from "react";
import { ContextMenu, MenuItem } from "react-contextmenu"
import UUID from "uuidjs";
import "./context-menu.scss";

const ExplorerContextMenu = props => {
    const contextNav = useRef();

    useEffect(() => {
        if (!renameInputRef.current) {
            return;
        }
        console.log("replace")
        renameInputRef.current.addEventListener =
            renameInputRef.current.addEventListener._original;
    }, [renameInputRef])

    const menuItems = useCallback(() => {
        return Object.entries(props.items).map(([text, handler], index) => 
             (<MenuItem key={UUID.genV1()} onClick={handler}>
                {text}
            </MenuItem>)
        )
    }, [props.items])

    return (
        <ContextMenu id={props.id} ref={contextNav}>
            {menuItems()}
        </ContextMenu>
    )
}

export default ExplorerContextMenu;
