import React, { useState }from "react";
import { connect } from "react-redux";
import { setPitch } from "../../../actions";
import Slider from "./../../common/Slider/Slider";
import "./fader-slider.scss";

const Fader = props => {
    let [isZero , setIsZero ] = useState(true);

    const indicatorClickHandle = () => {
        console.log('ser zero')
    }

    const sliderChangeHandle = (value, isTemp) =>{
        if(value > -0.5 && value < 0.5){
            setIsZero(true)
        } else {
            setIsZero(false);
        }
    }

    return (
        <div className={"fader" + ((props.className) ? " " + props.className  : "") } >
            <Slider className="fader" 
                horizontal 
                from={-10} 
                to={10} 
                step={1} 
                stickZero={0.5}
                onChange={ sliderChangeHandle } />
            <div className={"zero-indicator " + (isZero) ? "inidicator--active" : ""} onClick={indicatorClickHandle}/>
        </div>
    )
}
