import React from "react";
import {connect} from "react-redux";
import EqKnob from "./EqKnob/EqKnob"
import GainKnob from "./GainKnob/GainKnob";
import FilterKnob from "./FilterKnob/FitlerKnob";
import ResonansKnob from "./ResonasKnob/ResonansKnob";
import BinnaryButton from "./../../../common/BinnaryButton/BinnaryButton";
import PeakLevelMeter from "./PeakLevelMeter/PeakLevelMeter";
import {
    setGain,
    setLow,
    setMid,
    setHi,
    setSend,
    setFilter,
    setFilterResonans
} from "./../../../../../../actions";

import "./mixer-channel.scss";
import { throttle } from "./../../../../../../utils/functions/lodash"

class Channel extends React.Component{

    handleSetFilter =  (value) => {
        this.props.setFilter(value);
        if(value === 0){
            this.props.setFilterResonans(0);
        } 
    }

    render(){
        return (
            <div className={"mixer-channel channel-" + this.props.name }>

                <div className="knobs-set-1">
                    <EqKnob alt="Hi" className="eq-hi" onChange={ this.props.setHi }/>
                    <EqKnob alt="Mid" className="eq-mid" onChange={this.props.setMid }/>
                    <EqKnob alt="Low" className="eq-low" onChange={ this.props.setLow }/>

                </div>
                <PeakLevelMeter name={this.props.name}/>
                <div className="knobs-set-2">
                    <GainKnob className="eq-gain" onChange={ this.props.setGain }/>
                    <div className="mixer-group">
                        <ResonansKnob 
                            alt="RES" 
                            className="resonans" 
                            value={this.props.filterResonansValue}
                            onChange={ this.props.setFilterResonans } 
                            />
                        <FilterKnob 
                            alt="FL" 
                            className="filter" 
                            onChange={ this.handleSetFilter }/>
                    </div>
                </div>
                <div className="mixer-group group-fx">
                        <BinnaryButton 
                            className="btn-fx" 
                            value={this.props.send1}
                            onChange={this.props.setSend.bind(null, 1)}>
                                FX 1
                        </BinnaryButton>
                        <BinnaryButton 
                            className="btn-fx" 
                            value={this.props.send2}
                            onChange={this.props.setSend.bind(null, 2)}>
                                FX 2
                        </BinnaryButton>
                </div>
            </div>
        )
    }

}

const mapStateToProps = (state, ownProps) => ({
    send1 : state.mixer.channels[ownProps.name].sends[0],
    send2 : state.mixer.channels[ownProps.name].sends[1],

    filterResonansValue : state.mixer.channels[ownProps.name].filterResonans,
})

const mapDispachToProps = (dispatch, ownProps) =>({
    setGain : (value) =>  throttle(dispatch(setGain(ownProps.name, value)), 100),
    setHi : (value) => throttle(dispatch(setHi(ownProps.name, value)),100),
    setMid : (value) => throttle(dispatch(setMid(ownProps.name, value)),100),
    setLow : (value) => throttle(dispatch(setLow(ownProps.name, value)),100),
    setSend : (number, value) => throttle(dispatch(setSend(ownProps.name, number, value)),100),
    setFilter : (value) => throttle(dispatch(setFilter(ownProps.name, value)),100),
    setFilterResonans : (value) => throttle(dispatch(setFilterResonans(ownProps.name, value)),100)
})

export default connect(mapStateToProps, mapDispachToProps)(Channel);
