import React, { useMemo , useCallback, useRef} from 'react';
import Button from "react-bootstrap";
import EmotiContainer from "./EmotiContainer/EmotiContainer";

export default function(){
    const showEmotiContainer = useCallback((event)=>{
        let node = event.target.parentElement;
        let emoti = <EmotiContainer node={node} onSelect={e => console.log(e)}/>
    },[])

    const button = useMemo(()=>{
        return <Button onClick={showEmotiContainer}></Button>
    }, [])

    const textarea = useRef();


    return [textarea, button]
}
