import React, {useRef, useEffect, useMemo} from "react";
import WaveSurfer from "wavesurfer";
import style from "./wave-surfer-player.scss"

const WaveSurferPlayer = ({peaks, url}) => {
    const containerRef = useRef(null)

    const container = containerRef.current
    const ws = useMemo(()=>{
        return new Promise((res, rej)=> {
            if(container){
                const ws = WaveSurfer.create({
                   container: container.current,
                   barWidth: 5,
                   height: 100,
                   waveColor: style.waveColor,
                   progressColor: style.progressColor,
               })
               res(ws)
              // ws.load(null, peaks);
              } else rej(null)
        })
    }, [container])

    useEffect(()=>{
        console.log(ws)
        ws.then( ws => {
            ws.load((null, peaks))
        }).catch(()=>console.log("catch it kurwa"))
    }, [peaks, url ,ws])



    return (
        <div className="ws-player-container" ref={container}/>
    )
}

export default WaveSurferPlayer
