import getHeader from "./../../utils/auth/headers";
const url = process.env.REACT_APP_USER_API;

async function callQuery(query){
    console.log("call query")
    const authHeader = await getHeader();
    console.log(authHeader)
    const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...authHeader,
        },
        body: JSON.stringify({
          query: query,
        }),
      })
    let json = await response.json();
    return json;
}

export default {
  callQuery
}
