import {put, takeEvery} from 'redux-saga/effects';
import {ACTIONS} from "../actions/actions";
import {ytSearchAsync} from "./ytSearchSaga";


function* rootSaga(){
    yield takeEvery(ACTIONS.SEARCH_INPUT, ytSearchAsync);
}

export default rootSaga;
