import {  ACTIONS,  startRecording } from "../../actions";
import { takeEvery, select, put, putResolve,  } from "redux-saga/effects";


export default function* createRecordRequest() {
    yield takeEvery(ACTIONS.R_CREATE_RECORD_REQEST, handel)
}

const getToken = state => state.user.token



function* handel(action) {
    const token = yield select(getToken);

    if(token){
        console.log("start recoridn", action)
        yield put(startRecording(action.recName, 0))
    }

}
