import React, { useMemo, useState, useEffect, useCallback, useContext } from "react"
import { getApi } from "./../../../../apis/apiProvider";
import {PLAYBACK_STATE} from "./ListItem/PlaybackButton/PlaybackButton";

//getRecordUrl
export const PlayerContext = React.createContext({
    btnStateHandlers: {},
    setProgressHandlers:[],
    getProgressDataHandler: [],
    progressUpdaterHandler: null, 
    current : {
        id: null,
        start: 0,
        duration: 0,
        progress: 0,
        state : null,
    },

    setBtnCtrlHandler(id, handler){
        this.btnStateHandlers[id] = handler;
    },

    setBtnState(id, state){
        this.btnStateHandlers[id](state)
    },

    setProgressHandler(id, handler){
        if(this.setProgressHandlers[id]){
            this.setProgressHandlers[id].push(handler);
        }
        this.setProgressHandlers[id] = [handler];
    },

    setProgress(id, progress){
        for(let handler of this.setProgressHandlers[id]){
            handler(progress)
        }
        this.current.progress = progress;
    },
  
    setProgressDataHandler(id, handler){
        this.getProgressDataHandler[id] = handler;
    },

    getProgress(id){
        return this.getProgressDataHandler[id]();
    },

    getExteranalControler : () =>({
        current:this.current,
        mediaElement: this.mediaElement,
    })
})

export function useRecordPlayer(){
    const [mediaElement, setMediaElement] = useState(null);
    // const [current, setCurrent] = useState(null);
    // const [currentStart, setCurrentStart] = useState(0);
    const [progressUpdater, setProgressUpdater] = useState(null);
    const ctx = useContext(PlayerContext);

    const api = useMemo(()=>{
        return getApi('RecordsStore');
    }, [getApi])

    useEffect(()=>{
        const medEl = document.createElement('audio');
        medEl.autoplay = true;
       // medEl.preload = 'metadata'
        setMediaElement(medEl);
        ctx.mediaElement = medEl;
    }, [setMediaElement, ctx])

    const startUpdateProg = useCallback(()=>{
        const handler = setInterval(()=>{
            const current = ctx.current;
            if(!current.id || !current.duration){
                return;
            }
            const startTime = current.start * (current.duration/1000);
            const currTime = startTime + mediaElement.currentTime;
            const progress = currTime / (current.duration/1000);
            ctx.setProgress(current.id, progress);
        }, 200)
        ctx.progressUpdaterHandler = handler;
    }, [setProgressUpdater, mediaElement, ctx])

    const stopUpdateProg = useCallback(()=>{
        clearInterval(ctx.progressUpdaterHandler);
    }, [ctx])

    const playback = useCallback((id, pbState)=>{
        if(pbState === PLAYBACK_STATE.PLAY){
            const {progress, duration } = ctx.getProgress(id);
            mediaElement.src = api.getRecordUrl(id);
            const position = (duration / 1000) * progress;
            mediaElement.currentTime = position;
            mediaElement.play();
            if(ctx.current.id){
                ctx.setBtnState(ctx.current.id, PLAYBACK_STATE.PAUSE)
            } 
            ctx.current = {
                id,
                duration,
                start: 0,
                progress : progress,
                state: PLAYBACK_STATE.PLAY,
            }
            startUpdateProg()
        } else if(pbState === PLAYBACK_STATE.PAUSE){
            mediaElement.pause();
            ctx.current.state = PLAYBACK_STATE.PAUSE;
            stopUpdateProg(); 
        }
    }, [
        mediaElement, 
        api, 
        ctx, 
        stopUpdateProg, 
        startUpdateProg,
    ])

    const seek = useCallback(({id, filePosition, progress, duration}) => {
        stopUpdateProg();

    //     const bufferdTimeRanges = mediaElement.buffered;
    //    // const end = bufferdTimeRanges.length && bufferdTimeRanges.end(bufferdTimeRanges.length-1);
    //     if(bufferdTimeRanges.length){
    //         //console.log('seeekable',mediaElement.seekable.end(mediaElement.seekable.length - 1));
    //         console.log(bufferdTimeRanges.end(bufferdTimeRanges.length-1));
    //     }

        if(ctx.current.id && ctx.current.id !== id){
            ctx.setBtnState(ctx.current.id, PLAYBACK_STATE.PAUSE);
        }
        const position = (duration / 1000) * progress;
        if(ctx.current.id !== id){
            mediaElement.src = api.getRecordUrl(id);
            mediaElement.currentTime = position;
            ctx.setBtnState(id, PLAYBACK_STATE.PLAY);
            ctx.setProgress(id, progress);
            ctx.current = {
                id,
                start: 0,
                duration,
                state: PLAYBACK_STATE.PLAY
            }
        }

        mediaElement.currentTime = position;
        startUpdateProg();

    }, [ctx, api , mediaElement,stopUpdateProg, startUpdateProg])

    return [playback, seek, ctx.externalControler ]
}
