module.exports = blockAttrTokenizer;

function blockAttrTokenizer(eat, value, silent) {
    const match = /^{\.(\w+)}/.exec(value)

    if (match) {
        if (silent) {
            return true
        }

        return eat(match[0])({
            type: 'attrClass',
            value: match[1]
        })
    }
}
