import React, { useMemo, useCallback } from "react"
import { Button } from "react-bootstrap";
import "./pagin.scss";

const Pagin = ({ current, all, wide = 2, call }) => {

    const separator = useMemo(()=> <div className="pagin-separator"></div>, [])

    const button = useCallback((index, text) => {
        return <Button
            key={index}
            className={"pagin-btn" + ((index === current) ? " current" : "")}
            onClick={call && call.bind(null, index)}>
            {(text || index)}
        </Button>
    }, [call, current])

    const buttons = useMemo(() => {
        current = +current;
        let curr = button(current);
        let pre = [];
        let next = []
        if (current > 0) {
            let i;
            for (i = current - 1; i > 0; i--) {
                pre.unshift(button(i))
            }
            if (i > 0) {
                pre.push(separator())
                pre.unshift(button(0))
            }
        }
        if (current < all) {
            let i;
            for (i = current + 1; i <= all && i <= current + wide ; i++) {
                next.push(button(i))
            }
            if (i <= all) {
                next.push(separator())
                next.push(button(all))
            }
        }

        return [...pre, curr, ...next]
    }, [current, all, wide])

    return (
        <div className="pagin">{buttons}</div>
    )
}

export default Pagin
