import { ACTIONS } from "../../../actions/actions";
import React from "react";
import { connect } from "react-redux";

//----
import "./search-list.scss";


const SearchListItem = ( props ) => {

    const ytHref = id => "https://www.youtube.com/watch?v=" + id ;
    const formatTime = time => time.substr(2, time.length).toLowerCase();
    const stripHtml = html => {
        let doc = new DOMParser().parseFromString(html, 'text/html');
        return doc.body.textContent || "";
    }
    return(
        <li className="search-list-item">
            <div className="list-item-thumbnails">
                <img className="thumbnail-img" src={props.thumbnails.medium.url} />
                <span className="thumbnail-time">{formatTime(props.duration)}</span>
            </div>
            <div className="list-item-details">
                <h3>{stripHtml(props.title)}</h3>
                <p>{stripHtml(props.description)}</p>
                <a href={ytHref(props.id)}> {ytHref(props.id)} </a>
            </div>
        </li>
    )
}

class SearchList extends React.Component{
    constructor(){
        super();
    }

    render(){
        console.log(this.props.searchList[0]?.error)
        return (
            <div className="search-results">
                { this.props.searchList.error &&
                    <div>Connect to youtube problem</div>
                }
                <ul className="search-result-list">
                    { ( this.props.searchList && !this.props.searchList[0]?.error && this.props.searchList.map( item => <SearchListItem {...item} key={item.id}/> ) ) }
                </ul>
            </div>
        );
    }
};

const mapStateToProps = state => {
    //console.log(state.searchReducer.searchResults);
    return {
        searchList : state.searchReducer.searchResults,
    }
}

export default connect(mapStateToProps)(SearchList);
