import config from "./../../../../components/Deck/Player/configuration";
import WaveSurfer from "wavesurfer";
import { setChannelReady, setTimeLeft } from "./../../../../actions";
import store from "./../../../../store";

export default class ChannelBuilder {
  constructor() {
    this.dispatch = store.dispatch;
  }

  create(channelName, masterContaier, slaveContainer) {
    let channel = {};

    channel.masterConfig = config.master(masterContaier, channelName);
    channel.slaveConfig = config.slave(slaveContainer, channelName);

    channel.master = WaveSurfer.create(channel.masterConfig);
    channel.master.__proto__.loadWithEvent = function(...args) {
      this.fireEvent("load");
      this.load(...args);
    };
    channel.slave = WaveSurfer.create(channel.slaveConfig);

    channel.master.on("load", () => {
      this.clearState(channel);
    });

    channel.master.on("loading", progress => {
      //this.setLoading(progress); 
    });

    channel.master.on("ready", () => {
      channel.currentDuration = channel.master.getDuration();
      let resolution = 430;
      channel.slave.load(
        null,
        channel.master.backend.getPeaks(resolution, 0, resolution)
      );
      channel.setLoading(100);
      this.dispatch(setChannelReady(true, channelName))
      startSync();
    });


    return channel.master;
  }

  clearState(channel) {
    channel.slave.load(null, []);
    if (channel.barsElements) {
      channel.barsElements.forEach(htmlElement => htmlElement.remove());
    }
  }

  startSync = (channel) => {
    channel.syncHandle = setInterval(() => {
      channel.slave.drawer.progress(
        channel.master.getCurrentTime() / this.currentDuration
      );
    }, 100);
  };
}
