(function () {
    const vh = window.innerHeight * 0.01  * devicePixelRatio;
    document.documentElement.style.setProperty('--vh', `${vh}px`);
    window.addEventListener('resize', () => {
        const vh = window.innerHeight * 0.01 * devicePixelRatio;
        document.documentElement.style.setProperty('--vh', `${vh}px`)
    })
})()
