import React, { useState, useEffect } from "react";
import {reqRecOfUser} from "./../../actions"
import {connect} from "react-redux";
import { useParams, useHistory } from "react-router-dom";
import {useRecordPlayer} from "./../common/Hooks/useRecordPlayer";
import Record from "../common/components/Record/Record";

const UserRecord = ({requestList, recordsList, userId, countAll}) => {
    const [controls, player] = useRecordPlayer();
    const { title, id } = useParams();
    const history = useHistory()
    const [record, setRecord] = useState();

    useEffect(()=>{
        if(!title) { return }
     //   console.log(title, recordsList)
        let rec = recordsList?.filter( r => r.title === title)
        if(id){
            rec = rec.filter( r => r.id === id);
        }
      //  console.log("rec", rec)
        if(rec && rec.length === 0 || !rec){
           requestList(userId, 100, 0 , {title})
        }

        if(rec && rec.length === 1){
            setRecord(rec[0]);
            return;
        } 

        history.replace('/my/records?preloaded=true')

    }, [recordsList, setRecord, title, id, userId])

    useEffect(()=>{
        console.log(record)
    },[record])

    return (
        <div className="user-record">
            <Record  record={record} controls={controls} player={player} simpleHeader/>
            <div style={{width: "100%", height: "100%", color: "white"}}>
                Text
            </div>
        </div>
    )
}

const mapStateToProps = state => ({
    recordsList: state.records.list,
    countAll: state.records.countAll,
    userId: state.user.dbId,
})

const mapDispatchToProps = dispatch => ({
    requestList: (userId, pageSize, page, details) => dispatch(reqRecOfUser(userId, pageSize, page, details))
})

export default connect(mapStateToProps, mapDispatchToProps)(UserRecord)
