import React, {useMemo} from "react";
import {Col, Container, Row, Button} from "react-bootstrap";
import {useAuth0} from "./../../../auth0/react-auth0-spa";
import { useHistory } from "react-router-dom";
import "./encourage.scss";

const Encourage = () => {
    const {isAuthenticated, loading, loginWithPopup} = useAuth0();
    const history = useHistory();
    const animations = useMemo(() => ({
        "data-aos" : "zoom-in",
        "data-aos-once" : "true"
    }), [])

    const backgroundSvg = useMemo(() => (
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100">
            <path
                fill="#fafafa"
                d="m 0 7 q 9 17 26 16 q 22 -3 30 6 q 9 10 28 7 q 14 -3 28 0 q 9 4 21 5 v -41 h -133 z"
            ></path>
        </svg>
    ), []);

    const content = useMemo(()=>{
        if(loading) return;

        const title = {normal: "", light: ""};
        const button = {text: "", action: () => null};
        if(isAuthenticated){
            title.normal = "What you waiting for ??"
            title.light = "Don't waste your time and start crafting your talent"
            button.text = "Run application"
            button.action = () => history.replace('/console')
        } else {
            title.normal = "What you waiting for ??"
            title.light = "Don't waste your time and start crafting your talent"
            button.text = "Create account"
            button.action = () => loginWithPopup();
        }
        return (
            <>
                <div className="encourage-container" {...animations}>
                    <Col xs={12} lg={6}>
                        <h3 className="section-title">{title.normal} <span className="light" >{title.light}</span></h3>
                    </Col>
                    <Col xs={12} lg={6}>
                        <Button className="encourage-btn" onClick={button.action.bind(null)}>{button.text}</Button>
                    </Col>
                </div>
            </>
        )

    }, [loading, isAuthenticated])


    if(loading) return  null;

    return (
        <section className="home-encourage">
            <Container>
                <Row>
                    <>
                    {/*{backgroundSvg}*/}
                    {content}
                    </>
                </Row>
            </Container>
        </section>
    )
}

export default Encourage;
