import React, { useMemo, useCallback } from "react";
import classNames from "classnames";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPen } from "@fortawesome/free-solid-svg-icons";
import "./edit.scss";

const Edit = ({ active, type, children }) => {
    const buttonClasses = useMemo(() => classNames(
        'edit-btn',
        {
            "edit-btn--active": active,
            "edit-btn-before": (type === "text"),
            "edit-btn-over": (type === "image"),
        }
    ), [active])

    const catchBtnRef = useCallback((ref) => {
        ref.parentElement.classList.add("relative");
    }, [])

    return (
        <>
            {active && <button className={buttonClasses} ref={catchBtnRef}><FontAwesomeIcon icon={faPen} /></button>}
            {children}
        </>
    )
}

export default Edit;
