//Actions types 
export const ACTIONS = {
    ADD_LEFT_TRUCK : "Add left truck",
    ADD_RIGHT_TRUCK : "Add rigth truck",
    SEARCH_INPUT : "Tap in to Search component"
}

// actions creators

export function addLeftTruck(data){
    return {
        type: ACTIONS.ADD_LEFT_TRUCK,
        data
    }
}

export function addRightTruck(data){
    return {
        type: ACTIONS.ADD_RIGHT_TRUCK,
        data
    }
}
