import React, {useRef, useEffect, useState, useCallback} from "react";
import WaveSurfer from "wavesurfer";
import style from "./wave-surfer-player.scss";
import {PLAYBACK_STATE} from "./../PlaybackButton/PlaybackButton"

const WaveSurferPlayer = ({peaks,  id, seekHandler}) => {
    const containerRef = useRef(null)
    
    const [container, setContainerRef] = useState(null);
//    const [loaded, setLoaded] = useState(false);
 //   const [ws, setWs] = useState(null)

    const onContainerRef = useCallback( (ref) => {
        setContainerRef(ref);
    }, [setContainerRef])

    useEffect(()=>{
        if(container){
            const ws = new Promise((res, rej)=>{
                try{
                    const ws = WaveSurfer.create({
                        container: container,
                        barWidth: 5,
                        height: 100,
                        waveColor: style.waveColor,
                        progressColor: style.progressColor,
                    })
                    res(ws)
                } catch (err){
                    rej(err)
                }
            })

            ws.then((ws) =>{
                if(peaks){
                    ws.load(null, peaks);
                }
                ws.on('seek', (progress)=>{
                    seekHandler(id, progress)
                })
                //setWs(ws);
            })
        }
    }, [container, peaks ])


    // useEffect(()=>{
    //     if(playback  === PLAYBACK_STATE.PLAY && ws && url && peaks && !loaded){
    //         ws.load(url, peaks);
    //         setLoaded(true)
    //     } else if(playback === PLAYBACK_STATE.PLAY && loaded){
    //         ws.play();
    //     } else if(playback === PLAYBACK_STATE.PAUSE && loaded){
    //         ws.pause()
    //     }
    // }, [playback, peaks, url, ws, loaded])

    return (
        <div className="ws-player-container" ref={onContainerRef}/>
    )
}

export default WaveSurferPlayer
