import ACTIONS from "./../../actions";


const initState ={
    id : null,
    email : null,
    nickname : null,
}


function userReducer(state = initState, action){
    switch(action.type){
        case ACTIONS.SET_USER_PROFILE : {
            return {
                ...state, 
                ...action.user
            }
        }
        
        default : return state;
    }
}

export default configuraionReducer;
