import React, { useCallback, useRef, useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleDoubleLeft, faAngleDoubleRight } from "@fortawesome/free-solid-svg-icons";
import icons from "./emoji.json";
import style from  "./emoticons-style";
const iconsOnPanel = 30;

const Emoticons = ({ onSelect, toggle }) => {
    const [iconPanels, setPanels] = useState(null);
    const iconPanelsLength = useRef();
    const [display, setDisplay] = useState(false);
    const [currentSlide, setCurrentSlide] = useState(0);

    const createPanels = useCallback(() => {
        let panels = [];
        for (let i = 0; i < 500; i += iconsOnPanel) {
            let panel = [];
            for (let j = i; (j < iconsOnPanel + i) && (j < 500 ); j++) {
                let icon = icons[j]
                panel.push(
                    <button key={'icon-' + (i + j)} style={style.buttonStyle} onClick={onSelect.bind(null, icon.emoji)}>{icon.emoji}</button>
                )
            }
            panels.push(
                <section style={style.sectionStyle} className="emoticons-panel" key={'pannel-' + i}>
                    {panel}
                </section>
            )
        }
        iconPanelsLength.current = panels.length;
        return panels;
    }, [])

    const next = useCallback((event) => {
        event.preventDefault();
        setCurrentSlide(c => {
            console.log(c, iconPanelsLength.current)
            if (c + 1 < iconPanelsLength.current) {
                return c + 1;
            }
            return c;
        });
    }, [iconPanelsLength])

    const prev = useCallback((event) => {
        event.preventDefault();
        setCurrentSlide(c => {
            if (c - 1 > 0) {
                return c - 1;
            }
            return c;
        })
    }, [])

    const toggleHandler = useCallback(() => {
        if (!iconPanels) {
            setPanels(createPanels());
        }
        setDisplay(display => !display);
    }, [setDisplay, setPanels, iconPanels, createPanels])

    useEffect(() => {
        toggle.current = toggleHandler
    }, [toggle])

    const hide = useCallback(event => {
        if (!event.target.closest('.emoticons-container') &&
            !event.target.closest('.emoticon-btn')) {
                window.removeEventListener('mousedown', hide);
                setDisplay(false)
        }
    }, [setDisplay])

    useEffect(() => {
        if (display) {
            window.addEventListener('mousedown', hide);
        } else {
            window.removeEventListener('mousedown', hide)
        }
    }, [display, setDisplay, hide])

    return (
        <div className="emoticons-container emoticons-popup" style={{ ...style.containerStyle, display: (display) ? 'block' : 'none' }}>
            <button className="btn-prev" style={style.sliderNavigationButton} onClick={prev}>
                <FontAwesomeIcon icon={faAngleDoubleLeft} style={style.iconStyle} />
            </button>
            <div className="emoticons-slider" style={style.slideContainer}>
                <div className="emoticons-slides" style={{ transform: `translateX(-${currentSlide * 210}px)`, transition: 'transform 0.2s' }}>
                    {iconPanels}
                </div>
            </div>
            <button className="btn-prev" style={style.sliderNavigationButton} onClick={next}>
                <FontAwesomeIcon icon={faAngleDoubleRight} style={style.iconStyle} />
            </button>
        </div>
    )
}

export default Emoticons;
