import React, { useMemo , useCallback, useRef, Fragment} from 'react';
import {Button} from "react-bootstrap";
import EmotiContainer from "./EmotiContainer/EmotiContainer";

export default function(){

    const button = useMemo(()=>{
        const display = false;
        return (<div style={{position: 'relative', display:'inline-block',  zIndex:'5000'}}>
                    <Button style="emoticon-btn" onClick={() => display = !display }>ğŸ˜€</Button>
                    <EmotiContainer display={display} onSelect={e => console.log(e)}/>
                </div>)
    }, [])

    const textarea = useRef();


    return [textarea, button]
}
