import React from "react";
import UUID from "uuidjs";
import ClassName from "classnames"
import {useDoubleClick} from "./../useDoubleClick";

const DirElement = props => {
    const { name, path, open, empty } = props;

    let currentSelected = false;
    if ([...path, name].join("") === props.currentSelection.join("")) {
        currentSelected = true;
    }
    const liClassList = ClassName(
        "list-item item-dir",
        { "item-dir--empty": empty },
        { "item--selected": currentSelected }
    )
    let content = name;
    if (currentSelected && renameMode) {
        content = renameInput(name)
    }

    const [dirClickHandler] = useDoubleClick({
        clickHandler: props.onClick(path),// props.setSelection(path), //props.setSelection,
        doubleClickHandler: props.onDoubleClick(path)//props.toggleDir(path),//toggleDirHandler
    })


    return (
        <li
            onClick={dirClickHandler.bind(path)}
            //onContextMenu={props.setSelection.bind(null, [...path, name])}
            onContextMenu={props.onContextMenu.bind(null, path)}
            className={liClassList}
            data-path={path.join("/")}
            key={UUID.genV1()}>
            {!empty &&
                <button
                    className={"btn-dir " + ((open) ? "btn-dir--expand" : "btn-dir--colapse")}>
                    <FontAwesomeIcon
                        className="icon"
                        icon={(open) ? faAngleDown : faAngleRight}
                        onClick={toggleDirHandler.bind(null, [...path, name])} />
                </button>}
            <FontAwesomeIcon className="icon icon-dir" icon={faFolder} />
            {content}
        </li>
    )

}


const mapStateToProps = state => ({
    currentSelection: state.playList.currentSelection
})

export default connect(mapStateToProps)(DirElement);
