import React, { useCallback} from "react"
import RecordListItem from "./ListItem/RecordListItem";
import { useRecordPlayer } from "./useRecordPlayer";
import "./record-list.scss";


const RecordList = ({ list }) => {

    const [playback, seek , Context] = useRecordPlayer();

    const seekToPosition = useCallback((id, progress)=>{
        const fileSize = list.find((record) => record.id === id).fileSize;
        const position = ~~(parseInt(fileSize)  * progress);
        seek(id, position);
    }, [seek, list])

    return (
        <ul className="record-list">
            {list && list.map(item => {
                return <Context.Provider>
                            <RecordListItem  
                                key={item.id} 
                                item={item} 
                                seekHandler={seekToPosition}
                                playbackHandler={playback}/>
                        </Context.Provider>
            })}
        </ul>
    )
}

export default RecordList;
