import React, { useMemo , useCallback, useRef, useState} from 'react';
import {Button} from "react-bootstrap";
import EmotiContainer from "./EmotiContainer/EmotiContainer";

export default function(){
    const toggleRef = useRef();

    const onSelectIcon = useCallback((icon, event) => {
        event.preventDefault();
        toggleRef.current();
        console.log( icon)
    }, [])


    const component = useMemo(() => () => (
                <div className="emoticon" style={{position: 'relative', display:'inline-block',  zIndex:'5000'}}>
                    <Button  onClick={ () => toggleRef.current() }>ðŸ˜€</Button>
                    <EmotiContainer toggle={toggleRef} onSelect={onSelectIcon}/>
                </div>)
    , [toggleRef])

    const textarea = useRef();


    return [textarea, component]
}
