import React from "react";

export default function withMousePosition(WrappedComponent){

  return class extends React.Component {
    handleMousePostion(event){
        console.log(event.clientY, event.clientX)
    }

    render() {
      return (
          <div  className="with-mouse-wrapper" style={{display:"inline-block"}}>
            <WrappedComponent evnetsHandlers={{ onMouseMove: this.handleMousePostion }} {...this.props} />
         </div>
        );
    }
  };
};
