import React from "react";
import {Button} from "react-bootstrap";
import "./looper.scss";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';

class Looper extends React.Component {
    constructor() {
        super();
        this.state = {
            lengths: []
        }

        for(let i = 1 / 64; i < 64 ; i = i * 2){
            this.state.lengths.push(i);
        }
    }


    render(){
        const listItems = this.state.lengths.map( (length, index) =>{
            let display = length;
            if(length < 1){
                display = "1/" + (1/length)
            }
            return (<li className="slider-list-item" key={index} data-value={length}> 
                        {display} 
                    </li>)
        })

        return(
            <div className={"looper looper-deck-" + this.props.name}>
                <Button className="btn-in">IN</Button>
                <Button className="btn-out">OUT</Button>
                <div className="slider-container">
                    <FontAwesomeIcon icon={faCircle} className="ctr ctr-minus"/>
                    <FontAwesomeIcon icon={faCircle} className="ctr ctr-plus"/>
                    <ul className="slider-list">
                        {listItems.map(item => item)}
                    </ul>
                </div>
            </div>
        )
    }
}

export default Looper;
