function locateMention(value, fromIndex) {
    return value.indexOf('{.', fromIndex)
}

tokenizeHtmlClass.notInLink = true
tokenizeHtmlClass.locator = locateMention

function tokenizeHtmlClass(eat, value, silent) {
    console.log("value", value)
    var match = /^(.*){\.(\w+)}/.exec(value)

    if (match) {
        console.log("get match")
        if (silent) {
            return true
        }

        let type = 'breal';
        switch (match[1]){
            case "***" : {
                type = 'thematicBreak';
                break;
            }
        }
        //const add = eat(match[0]);

        return eat(match[0])({
            // type: 'htmlClass',
            type:  type,
            className: match[2],
        })
/*        return add({
             type: 'link',
            // url: 'https://social-network/' + match[1],
            // children: [{type: 'text', value: match[0]}]
            data: {
                className: 'custom-block-heading',
                hProperties: {
                    className: 'custom-block-heading',
                }
            }
        })*/
    }
}

module.exports.tokenizeHtmlClass = tokenizeHtmlClass;
