import React, {useEffect, useState, useCallback, useRef} from "react";
import Testimonial from "./Testimonial";
import Slider from "./../../../common/components/Carousel-slider/Slider";
import {Container, Row, Col} from "react-bootstrap"
import "./testimonials.scss";
import SliderControl from "../../../common/components/Carousel-slider/SliderControls";

const getTestimonials = () => require.context('./testimonials', true, /\.json$/).keys();

const Testimonials = () => {
    const [testimonials, setTestimonials] = useState(null);
    const slideWrapper = useRef();
    const [nextSlideHandle, setNextHandle] = useState(null);
    const [prevSlideHandle, setPrevHandle] = useState(null);
    const shift = useRef();

    const testimonialsJsons = useCallback(() => getTestimonials().map(file => {
        return import("./testimonials" + file.substr(1, file.length));
    }), []);

    useEffect(() => {
        if (!testimonialsJsons) return;
        Promise.all(testimonialsJsons())
            .then(jsons => jsons.map(module => module.default))
            .then(testimonials => {
                setTestimonials(testimonials)
            })
    }, [testimonialsJsons, setTestimonials])


    const changeSlide = useCallback(direction => {
        if (!slideWrapper.current) return;
        if (!shift.current) shift.current = 0;

        const length = testimonials.length;

        switch (direction) {
            case "next": {
                console.log(shift.current, length)
                slideWrapper.current.append(slideWrapper.current.children[Math.abs(shift.current % (length))].cloneNode(true))
                shift.current++;
                break;
            }
            case "prev": {

                //slide.current--;
                console.log(length, shift.current, length - Math.abs(shift.current % length))
                slideWrapper.current.prepend(slideWrapper.current.children[length - Math.abs(shift.current % length)].cloneNode(true))
                //shift.current--;
                break;
            }
            default:
                return null;
        }


        [...slideWrapper.current.children].forEach(element =>
            element.style.transform = `translateX(${(shift.current * -100)}%)`
        );

    }, [shift, slideWrapper, testimonials])

    if (!testimonials) return null;
    return (
        <section className="home-testimonial">
            <Container>
                <div className="testimonial-slider">
                    <Row className="slide-wrapper" ref={slideWrapper}>
                        <Col sm={12}>
                            <Slider
                                slides={testimonials.map(tst => <Testimonial data={tst}/>)}
                                next={handle => setNextHandle(handle)}
                                prev={handle => setPrevHandle(handle)}
                            />
                        </Col>
                    </Row>
                </div>
                <Row>
                    <Col sm={12}>
                        <div className="testimonial-slider-control">
                            <SliderControl
                                next={() => console.log("next")}
                                prev={() => console.log("prev")}/>
                        </div>
                    </Col>
                </Row>
            </Container>
        </section>
    )
}

export default Testimonials;
