const apiUrl = process.env.REACT_APP_YT_API_URL;


export async function search(query = ""){
    //console.log(url);
    const comand =  "/search?q=";
    const options = ['&maxResults=10'].join('');
    const url = apiUrl + comand + encodeURI(query) + options;

    let response = await fetch(url);
    let json = await response.json();
    return parse(json);
}

function parse (json){
    let results = [];
    for(let item of json.items){
        results.push({
            id : item.id.videoId,
            etag : item.etag,
            title : item.snippet.title,
            description: item.snipet.description,
            thumbnails : item.snipet.thumbnails
        })

    }
    
}
