import React, {useState, useCallback} from "react";
import {Form} from "react-bootstrap";
import "./record-search.scss"

const RecordSearch = ({onSearch}) =>{
    const [query, setQuery] = useState("")

    const setQueryHandler = useCallback( event =>{
        setQuery(event.target.value)
    }, [setQuery])

    const onKeyDown = useCallback( event => {
        if(event.key === "Enter" && onSearch){
            onSearch(query)
        }
    }, [onSearch, query]) 

    return (
        <div className="record-serach">
            <div className="record-search-container">
                <h4>Dj Truck records</h4>
                <Form.Control 
                    className="record-search-control" 
                    type="text" 
                    value={query} 
                    onChange={setQueryHandler}
                    onKeyDown={onKeyDown}/>
                <div className="search-options">
                    <Form.Check 
                        className="record-serach-options" 
                        inline 
                        label="titles" 
                        type="checkbox" 
                        id="serach-opt-title"/>
                    <Form.Check 
                        className="record-serach-options" 
                        inline 
                        label="artist" 
                        type="checkbox" 
                        id="serach-opt-artis"/>
                    <Form.Check
                        className="record-serach-options" 
                        inline 
                        label="description" 
                        type="checkbox" 
                        id="serach-opt-description"/>
                </div>
            </div>
        </div>

    )
}


export default RecordSearch;
