import React, { useEffect, useContext, useState, useRef } from "react";
import { connect } from "react-redux";
import { reqRecOfUser } from "./../../actions"
import RecordsList from "./../common/components/RecordList/RecordList";
import { useRecordPlayer } from "./../common/Hooks/useRecordPlayer";
import PlayerControls from "./../common/components/PlayerControls/PlayerControls";
import LayoutContext from "./../common/Layout/LayoutContext";
import { DropdownButton, Dropdown } from "react-bootstrap";
import { useParams, useHistory } from "react-router-dom";
import "./user-records.scss"

const UserRecords = ({ reqestList, userId, recordsList }) => {
    const history = useHistory();
    let { pageSize, page } = useParams();
    const [_pageSize, setPageSize] = useState(20);

    const timeoutRef = useRef();

    useEffect(() => {
        if(!userId){
            timeoutRef.current = setTimeout(()=>{
                history.replace("/")
            }, 3000)
            
            return;
        }
        if(userId){
            clearTimeout(timeoutRef.current);
        }
        if(!pageSize && !page){
            reqestList(userId, _pageSize, 1);
            return;
        }
        if(pageSize && page){
            page = pageSize
            reqestList(userId, pageSize, page);
            setPageSize(pageSize)
        }

    }, [userId, reqestList])

    const layoutContext = useContext(LayoutContext);

    useEffect(() => {
        layoutContext.setFooter(false);
    }, [layoutContext])

    const [controls, player] = useRecordPlayer();

    return (
        <div className="user-records">
            <div className="user-records-top-bar">
                <h2 className="title">Your records</h2>
                <DropdownButton
                    alignRight
                    className="btn-pagger"
                    title={_pageSize + " on page "}>
                    <Dropdown.Item onClick={setPageSize.bind(null, 10)}> 10 </Dropdown.Item>
                    <Dropdown.Item onClick={setPageSize.bind(null, 20)}> 20 </Dropdown.Item>
                    <Dropdown.Item onClick={setPageSize.bind(null, 30)}> 30 </Dropdown.Item>
                    <Dropdown.Item onClick={setPageSize.bind(null, 50)}> 50 </Dropdown.Item>
                    <Dropdown.Item onClick={setPageSize.bind(null, 75)}> 75 </Dropdown.Item>
                    <Dropdown.Item onClick={setPageSize.bind(null, 100)}> 100 </Dropdown.Item>
                </DropdownButton>
            </div>

            <RecordsList
                list={recordsList}
                controls={controls}
                player={player} />
            <PlayerControls
                list={recordsList}
                controls={controls}
                player={player} />
        </div>
    )
}

const mapStateToProps = state => ({
    recordsList: state.records.list,
    userId: state.user.dbId,
})

const mapDispatchToProps = dispatch => ({
    reqestList: (userId, perPage,  page) => dispatch(reqRecOfUser(userId, perPage, page))
})

export default connect(mapStateToProps, mapDispatchToProps)(UserRecords)
