import React, { useEffect , useRef} from "react";
import { connect } from "react-redux";
//import WaveSurfer from "wavesurfer";

import WaveSurfer from "./WaveSurfer/WaveSurfer";

import { analyze } from 'web-audio-beat-detector';

import youTubeApi from "./../../apis/yt/ytApi";

const getApi = (apiName) =>{
    switch(apiName){
        case "YouTube": return youTubeApi;

        default : return null;
    }
}

class Deck extends React.Component {
    constructor(){
        super();
        this.mainWave = React.createRef();
        this.smallWave = React.createRef();

        this.state = {}
        this.state.join = {
            peaks: [],
            position: 0,
        }
    }


    componentDidMount(){
       /* if(!this.props.active) return;
        this.bigWaveSurfer = WaveSurfer.create({
            container: this.mainWave.current,
            waveColor: 'violet',
            progressColor: 'purple',
            autoCenter : true,
            scrollParent: true,
            hideScrollbar:true,
            fillParent: false,
            height: 130,
            minPxPerSec : 150
        });

        this.smallWaveSurfer = WaveSurfer.create({
            container: this.smallWave.current,
            fillParent: true,
            scrollParent : false,
            hideScrollbar: true,
            scrollParent : false,
            height: 50,
        })

        let duration = 0;
        this.bigWaveSurfer.on('ready', () => {
           // this.bigWaveSurfer.play();
            duration = this.bigWaveSurfer.getDuration();
            let resolution = 430;
            let peaks = this.bigWaveSurfer.backend.getPeaks(resolution, 0, resolution);
            this.smallWaveSurfer.load(null, peaks);
            startSync();
            //this.calcBmp();
        });

        this.smallWaveSurfer.on('seek', (progress) => {
            this.bigWaveSurfer.seekTo(progress);
            if(!this.bigWaveSurfer.isPlaying()){
                this.bigWaveSurfer.play();
            }
        })

        let syncHandle = null;
        let startSync = () => {
            syncHandle = setInterval(() => {
                let progress = this.bigWaveSurfer.getCurrentTime() / duration;
                this.smallWaveSurfer.drawer.progress(progress);
            }, 100);
        }

        let url = "https://ciagnik-yt-downloader.herokuapp.com/download?url=https://www.youtube.com/watch?v=hVv331iLMXM";
        this.bigWaveSurfer.load(url);

        this.calcBmp = () => {
            fetch(url)
            .then( response => response.arrayBuffer())
            .then( buffer => {
                const audioContext = new AudioContext();
                return audioContext.decodeAudioData(buffer);
            }).then( audioBuffer => analyze(audioBuffer))
            .then(tempo => console.log(tempo))

        }
        //analyze()*/
    }

    peaksFromMaster(peaks){
        this.setState( state => {
            const _state = {...state}
            _state.join.peaks = peaks;
            return _state;
        })
    }

    positionFromMaster(position){
        this.setState( state => {
            const _state = {...state};
            _state.join.position = position;
            return _state;
        })
    }

    render() {
        let api = getApi(this.props.track.source);
        let url = api && api.getUrl(this.props.track.id) || null;

        return (
            <div style={{height: 250 + 'px'}}>
                <WaveSurfer
                    type="master" 
                    height="130" 
                    url={url} 
                    name={ this.props.name } 
                    getPeaksHandler={ this.peaksFromMaster.bind(this) }
                    getPositionHandler = { this.positionFromMaster.bind(this) }
                    />        
                <WaveSurfer type="slave" height="50" peaks={ this.state.join.peaks } name={ this.props.name } />    
            </div>
            )
        }
    
}

const mapStateToProps = (state, ownProps) => ({
    track : state.console.deck[ownProps.name].track,
    state : state.console.deck[ownProps.name].state,
})

const mapDispachToProps = dispach => ({
    
})

export default connect(mapStateToProps)(Deck);
