import { ACTIONS } from "../../actions";
import { put, call, select, takeEvery } from 'redux-saga/effects';

import { getApi } from "./../../apis/apiProvider";


export default function* watcher() {
    yield takeEvery(ACTIONS.U_REQ_UPDATE_PICTURE, handel);
}

const getToken = state => state.user.token

function* handel(action) {
    const token = yield select(getToken);

    try {
        const { callQueryUploadSingle, queries } = getApi("UserAssets");

        const query = queries.updateUserPictureQl;
        const variables = { file: action.file };
        const result = yield callQueryUploadSingle(query, token, variables);

        console.log(result);

        if (!result.errors) {
            //  yield put(setRecData(recordData))
        } else {
            throw new Error("Can't load record data from database")
        }
    } catch (e) {
        console.log(e.message)
    }
}
