import React from "react";


const withRefSize = (WrappedComponent) => {

    return class extends React.Component{


        componentDidMount() {
            console.log('tiis.refs', this)
        }

        importRef = ( name, ref) => {
            console.log(ref)
        }

        render(){
            return <WrappedComponent {...this.props}  exportRef={this.importRef}/>
        }
    } 

}

export default withRefSize;
