import React, { useEffect, useState } from "react";

const getTestimonials = () => {
    function importAll (r) {
        return r.keys();
    }
    return importAll(require.context('./testimonials', true, /\.json$/));
}

const Testimonials = () => {
    const [testimonials, setTestimonials] = useState(null);
    const testimonialsJsons = getTestimonials().map( file => {
        return import("./testimonials" + file.substr(1, file.length));
    });



    useEffect(()=>{
        if(!testimonialsJsons) return;
        Promise.all(testimonialsJsons).then( jsons => setTestimonials(jsons))
    }, [testimonialsJsons, setTestimonials])

    if(!testimonials) return null;

    console.log(testimonials)

    return (
        <div className="home-testmionial">
            <div className="testimonial-slider">

            </div>
            <div className="testimonail-slider-control">

            </div>
        </div>
    )
}

export default Testimonials;
