import React from 'react';
import { connect } from "react-redux";

import { FormControl } from "react-bootstrap"
import SearchList from './serachList/SearchList.js';


import { searchInput } from "./../../actions/actions.js";

import "./search.scss";


const Search = props => {
    const searchInputHandler = props.searchInput;
    return (
        <div className>
           <FormControl className="search-control"
           type="text" 
           placeholder="Tap in to serach on youtube"
           onChange={ event => searchInputHandler(event.target.value) }
           />
           <SearchList/>
        </div>
    );
}

const mapDispachToProps = dispach => ({
    searchInput : (text) => dispach(searchInput(text))
})

export default connect(null, mapDispachToProps)(Search);
