export default  (userId, pageSize, page) => `
    query{
        records(input : {
            userId: ${userId}
            pagin: {
                pageSize: ${pageSize}
                page: ${page}
            }
        }){
            records {
                id
                title
                description
                generes  {
                    name
                }
                user {
                    id
                    nickname
                }
                peaks
                duration
                fileSize 
            }
            countAll
        } 
    }
`
