function locateMention(value, fromIndex) {
    return value.indexOf('{.', fromIndex)
}

tokenizeCssClass.notInLink = true
tokenizeCssClass.locator = locateMention

function tokenizeCssClass(eat, value, silent) {
    console.log("the this in tokeniser", this)
    Object.assign(this, this.parser);
    this.locator = locateMention;
    this.notInLink = true;
    console.log("value", value)
    var match = /^{\.(\w+)}/.exec(value)

    if (match) {
        if (silent) {
            return true
        }
       // let value = match[0].replace(match[1], value)
        return eat(match[0])({
            type:  "cssClass",
            className: match[1],
        })

    }
}

module.exports.tokenizeCssClass = tokenizeCssClass;


