import React, { useEffect, useContext, useState } from "react";
import { connect } from "react-redux";
import { reqRecOfUser } from "./../../actions"
import RecordsList from "./../common/components/RecordList/RecordList";
import { useRecordPlayer } from "./../common/Hooks/useRecordPlayer";
import PlayerControls from "./../common/components/PlayerControls/PlayerControls";
import LayoutContext from "./../common/Layout/LayoutContext";
import { DropdownButton, Dropdown } from "react-bootstrap";
import { useParams , useHistory } from "react-router-dom";
import "./user-records.scss"

const UserRecords = ({ reqestList, userId, recordsList }) => {
  //  const history = useHistory();
 //   let { perPage, page } = useParams();
    const [_perPage, , setPerPage] = useState(20);

    useEffect(() => {
    //     if(userId){
    //    //     history.replace("/")
    //     }
    //     if(perPage && !page){
    //         page = perPage
    //     }
        reqestList(userId)
    }, [userId, reqestList])

    const layoutContext = useContext(LayoutContext);

    useEffect(() => {
        layoutContext.setFooter(false);
    }, [layoutContext])

    const [controls, player] = useRecordPlayer();

    return (
        <div className="user-records">
            <div className="user-records-top-bar">
                <h2 className="title">Your records</h2>
                <DropdownButton
                    alignRight
                    className="btn-pagger"
                    title={_perPage,  + " on page "}>
                    <Dropdown.Item onClick={setPerPage.bind(null, 10)}> 10 </Dropdown.Item>
                    <Dropdown.Item onClick={setPerPage.bind(null, 20)}> 20 </Dropdown.Item>
                    <Dropdown.Item onClick={setPerPage.bind(null, 30)}> 30 </Dropdown.Item>
                    <Dropdown.Item onClick={setPerPage.bind(null, 50)}> 50 </Dropdown.Item>
                    <Dropdown.Item onClick={setPerPage.bind(null, 75)}> 75 </Dropdown.Item>
                    <Dropdown.Item onClick={setPerPage.bind(null, 100)}> 100 </Dropdown.Item>
                </DropdownButton>
            </div>

            <RecordsList
                list={recordsList}
                controls={controls}
                player={player} />
            <PlayerControls
                list={recordsList}
                controls={controls}
                player={player} />
        </div>
    )
}

const mapStateToProps = state => ({
    recordsList: state.records.list,
    userId: state.user.dbId,
})

const mapDispatchToProps = dispatch => ({
    reqestList: (userId) => dispatch(reqRecOfUser(userId))
})

export default connect(mapStateToProps, mapDispatchToProps)(UserRecords)
