import { ACTIONS } from "./../../actions";

const initState = {
    recording : false,
    curretnRecName: ""
 
}

export default function headerState(state = initState, action) {
    console.log(state.recording);
    switch (action.type) {
        case ACTIONS.R_START_RECORDING : {
            return {
                ...state,
                recording: true,
                curretnRecName: action.recName
            }
        }

        case ACTIONS.R_END_RECORDING: {
            return {
                ...state,
                recording: false,
                curretnRecName: ""
            }
        }


        default: return state;
    }
}
