import React, { Fragment } from "react";
import { connect } from "react-redux";
import LoadingBar from 'react-redux-loading-bar';
import NavBar from "./NavBar/NabBar"
import { Col, Row, Container } from "react-bootstrap";
import "./header.scss";

class Header extends React.Component {
  constructor() {
    super();
    this.barElement = React.createRef(null);
    this.scrollDirection = 0; // 1 down -1 up;
    this.prevScrollPosition = 0;
  }

  startWatching(){
    window.addEventListener('scroll', this.scrollWatching.bind(this))
  }

  stopWatching(){
    window.removeEventListener('scroll', this.scrollWatching.bind(this))
  }

  scrollWatching(event){
    //console.log(window.scrollY)
    this.scrollDirection = this.prevScrollPosition - window.scrollY;
    this.prevScrollPosition = window.scrollY
    console.log(this.scrollDirection)
  }

  updateState(prevProps){
    if(this.props.sticki){
      this.startWatching();
    } else {
      this.stopWatching();
    }
  }

  componentDidMount(){
    this.updateState();
  }

  componentDidUpdate(prevProps){
    this.updateState(prevProps);

  }

  render() {
    return (
      <Fragment>
        <LoadingBar className="loading-bar" />
        <header className="top-bar" ref={this.barElement}>
          <Container className="app layout container-xl" >
            <Row>
              <Col>
                <NavBar className="navigation-bar" />
              </Col>
            </Row>
          </Container>
        </header>
      </Fragment>
    )
  }
}

const mapStateToProps = state => ({
  sticki : state.header.sticki,
  hidden : state.header.hidden,
  disabled: state.header.disabled,
})

export default connect(mapStateToProps)(Header);
