import React, {useMemo, useCallback} from "react"
import { toDate, formatRelative as DateFormatRelative } from "date-fns";
import * as locales from 'date-fns/locale';

export function useFormatRelative() {

    // const dateFormated = useMemo(() => {
    //     const timeZoneOffset = new Date().getTimezoneOffset();
    //     function getLang() {
    //         if (navigator.languages != undefined)
    //             return navigator.languages[0];
    //         else
    //             return navigator.language;
    //     }
    //     return formatRelative(
    //         toDate(timestamp - (timeZoneOffset * 1000)),
    //         new Date(),
    //         // {
    //         //     locale: locales[getLang().substr(0,2)]
    //         // }
    //     )
    // }, [timestamp])

    const timezoneOffset = useMemo(() => {
        return new Date().getTimezoneOffset() * 1000;
    }, [])

    const getLocal = useCallback(() => {
        let lang = "";

        if (navigator.languages != undefined){
            lang =  navigator.languages[0];
        }
        else{
            lang =  navigator.language;
        }
        
        lang = lang.substr(0,2);
        return locales[lang];
    })

    const formatRelative = useCallback((timestamp, { timezone, local } = {}) => {
        const time = timezone ? timestamp - timezoneOffset() : timestamp;
        const opt = local ? {locale: getLocal()} : {};
        const now = new Date();
        return DateFormatRelative(toDate(time), now , opt);
    },[timezoneOffset, getLocal])

    return [formatRelative]
}
