import { ACTIONS } from "./../../actions";

export const STATES = {
    INIT : "Recording procecess initited",
    RECORDING : "Recording porcess runing",
    FAIL : "Initiating process fail",
    IDLE: "Recordign process is finished or not started"
}

const initState = {
   // recordingInitiated : false,
   // recording : false,
    recName: "",
    recId: null,
    state: STATES.INIT,
}

export default function headerState(state = initState, action) {
   // console.log(state.recording);
    switch (action.type) {
        case ACTIONS.R_START_RECORDING : {
            return {
                ...state,
                recordingInitiated: true,
                recName: action.recName,
                recId: action.id
            }
        }

        case ACTIONS.R_END_RECORDING: {
            return {
                ...state,
                recording: false,
                recordingInitiated: false,
                recName: ""
            }
        }

        case ACTIONS.R_RECORDING: {
            return {
                ...state,
                recording: true,
            }
        }

        default: return state;
    }
}
